import React from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate} from 'react-router-dom';
import { Login } from '../Login';
import { HomeGerente } from '../HomeGerente';
import { HomeFuncionario } from '../HomeFuncionario';

function App() {

  const isAuthenticated = true; // Altere isso para verificar se o usuário está autenticado ou não

  function PrivateRoute({ element: Component, ...rest }) {
    alert("TRueeeee")
    return (
      <Route
        {...rest}
        element={
          isAuthenticated ? (
            <Component />
          ) : (
            <Navigate to="/" replace />
          )
        }
      />
    );
  }

  return (
      <Router>
        <Routes>
            <Route element={<Login />} path="/" exact /> 
            <PrivateRoute element={<HomeGerente />} path="/HomeGerente" exact />
            <PrivateRoute element={<HomeFuncionario />} path="/HomeFuncionario" exact />
        </Routes>
      </Router>
  );

}

export default App;
