import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes} from 'react-router-dom';
import { Login } from '../Login';
import { HomeGerente } from '../HomeGerente';
import { HomeFuncionario } from '../HomeFuncionario';

function App() {
    const [isAuthenticated, setIsAuthenticated] = useState(0);

    function handleStatusLoginChange(newStatusLogin) {
        setIsAuthenticated(newStatusLogin);
    }

    const defaultRoute = (
        <>
            {isAuthenticated === 1 && <Route element={ <HomeGerente /> } path="/" exact />}
            {isAuthenticated === 2 && <Route element={ <HomeFuncionario /> } path="/" exact />}
            {isAuthenticated === 0 && <Route element={ <Login statusLogin={handleStatusLoginChange} /> } path="/" exact />}
        </>
    );

    return (
        <Router>
            <Routes>{defaultRoute}</Routes>
        </Router>
    );
}

export default App;
