import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, useHistory } from 'react-router-dom';
import { Login } from '../Login';
import { HomeGerente } from '../HomeGerente';
import { HomeFuncionario } from '../HomeFuncionario';

function App() {    
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const history = useHistory();

    const handleStatusLoginChange = (newStatusLogin) => {
        setIsAuthenticated(newStatusLogin === 1 || newStatusLogin === 2);
    };

    if (isAuthenticated) {
        history.push('/home');
    }

    return (
        <Router>
            <Routes>
                <Route element={<Login statusLogin={handleStatusLoginChange} />} path="/" exact />
                <Route element={<HomeGerente />} path="/home" exact />
                <Route element={<HomeFuncionario />} path="/home" exact />
            </Routes>
        </Router>
    );
}

export default App;
