import styled from "styled-components";
import fundo from "../../Imagens/fundo.jpg"

export const Geral = styled.section`
    width: 100vw;
    height: 100vh;
    
    background-image: url(${fundo});
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    overflow: hidden;
`;


export const Container = styled.section`
    width: 400px;
    margin: 100px auto;
    box-shadow: 0 0 1em #6c757d;
    padding: 30px;
    border-radius: 40px;
    background-color: aliceblue;
`;

export const Imagemtopo = styled.section`
    width: 40px;
    height: 40px;
    background-color: aliceblue;
    border-radius: 100px;
`;


export const ConteudoTitulo = styled.section`
    display: flex;
    justify-content: space-between;
`;

export const BotaoAcao = styled.section`
    margin: 30px 0px;
`;

export const ButtonInfo = styled.button`
    background-color: #EEEEEE;
    color: #008BD6;
    padding: 8px 12px;
    border-radius: 4px;
    border: 1px solid #008BD6;
    cursor: pointer;
    font-size: 18px;
    
    :hover{
        color: #fff;
        background-color: #008BD6;
    }
`;

export const Titulo = styled.h1`
    font-size: 23px;
    color: #3e3e3e;
    text-align: center;
    padding: 10px;
`;

export const AlertSuccess = styled.p`
    background-color: #B8FFDF;
    color: #0f5132;
    margin: 20px 0px;
    border: 1px solid #badbcc;
    border-radius: 4px;
    padding: 7px;
`;

export const AlertDanger = styled.p`
    background-color: #d1e7dd;
    color: #510F0F;
    margin: 20px 0px;
    border: 1px solid #DBBABA;
    border-radius: 4px;
    padding: 7px;
`;


export const ConteudoForm = styled.section`
    max-width: 960px;
    padding: 10px 30px 30px;
`;

export const Form = styled.form`
    margin: 0px auto;
`;

export const Label = styled.label`
    width: 100%;
    padding: 12px;
    margin-top: 6px;
    margin-bottom: 16px;
`;


export const Input = styled.input`
    width: 100%;
    padding: 12px;

    border: 1px solid #ccc;
    border-radius: 4px;
    box-sizing: border-box;
    margin-top: 16px;
    resize: vertical;

    margin-top: 6px;
    margin-bottom: 16px;
`;

export const ButtonSuccess = styled.button`
    background-color: #fff;
    color: #198754;
    padding: 8px 12px;
    border-radius: 4px;
    border: 1px solid #198754;
    cursor: pointer;
    font-size: 18px;
    
    :hover{
        color: #fff;
        background-color: #198754;
    }
`;
