import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import { Login } from '../Login';
import { HomeGerente } from '../HomeGerente';
import { HomeFuncionario } from '../HomeFuncionario';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(0);

  const handleStatusLoginChange = (newStatusLogin) => {
    setIsAuthenticated(newStatusLogin);
    alert(newStatusLogin);
  };

  const defaultRoute = (
    <>
      {isAuthenticated === 1 && <Route element={<HomeGerente />} path="/home-gerente" exact />}
      {isAuthenticated === 2 && <Route element={<HomeFuncionario />} path="/home-funcionario" exact />}
      {isAuthenticated === 0 && <Route element={<Login statusLogin={handleStatusLoginChange} />} path="/" exact />}
      {/* Redireciona para a página de login se o usuário não estiver autenticado */}
      {isAuthenticated === 1 && <Navigate to="/home-gerente" replace />}
      {isAuthenticated === 1 && <Navigate to="/home-gerente" replace />}
    </>
  );

  return (
    <Router>
      <Routes>{defaultRoute}</Routes>
    </Router>
  );
}

export default App;
