import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes} from 'react-router-dom';
import { Login } from '../Login';
import { HomeGerente } from '../HomeGerente';
import { HomeFuncionario } from '../HomeFuncionario';

function App() {
    const [isAuthenticated, setIsAuthenticated] = useState(0);

    const handleStatusLoginChange = (newStatusLogin) => {
        setIsAuthenticated(newStatusLogin);
    };

    useEffect(() => {
        let defaultRoute;

        if (isAuthenticated === 1) {
            defaultRoute = <Route element={<HomeGerente />} path="/" exact />;
        } else if (isAuthenticated === 2) {
            defaultRoute = <Route element={<HomeFuncionario />} path="/" exact />;
        } else {
            defaultRoute = <Route element={<Login statusLogin = { handleStatusLoginChange } />} path="/" exact />;
        }

        setDefaultRoute(defaultRoute);
    }, [isAuthenticated]);

    const [defaultRoute, setDefaultRoute] = useState(null);

    return (
        <Router>
            <Routes>
                {defaultRoute}
            </Routes>
        </Router>
    );
}

export default App;
