import React from 'react';
import { BrowserRouter as Router, Route, Routes} from 'react-router-dom';
import { Login } from '../Login';
import { HomeGerente } from '../HomeGerente';
import { HomeFuncionario } from '../HomeFuncionario';

function App() {
    const [login, setData] = useState('');

    const statusLogin  = (statusLogin) =>{
        setData(statusLogin);
        //alert(statusLogin);
    }

  const isAuthenticated = 1; // Altere isso para verificar se o usuário está autenticado ou não

  let defaultRoute;

  if (isAuthenticated === 1) {
    defaultRoute = <Route element={<HomeGerente />} path="/" exact />;
  } else if (isAuthenticated === 2) {
    defaultRoute = <Route element={<HomeFuncionario />} path="/" exact />;
  } else {
    defaultRoute = <Route element={<Login statusLogin={statusLogin} />} path="/" exact />;
  }

  return (
    <Router>
      <Routes>
        {defaultRoute}
      </Routes>
    </Router>
    );
}

export default App;
