import React, { useState } from 'react';


import { Imagemtopo, Geral, Titulo, ConteudoForm, AlertSuccess, AlertDanger, Form, Container, Label, Input, ButtonSuccess,  ConteudoTitulo} from './styles';


export function Login({statusLogin}){

  const [usuario, setUsuario] = useState({
    user: '',
    senha: ''
  })

  const [status, setStatus] = useState({
    type: '',
    mensagem: ''
  })


  
  const valorInput = e => setUsuario({ ...usuario, [e.target.name]: e.target.value});

  const Login = async e =>{
    e.preventDefault();
    

    await fetch("http://localhost/api-crud-php/login.php", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({usuario})
    })
    .then((response) => response.json())
    .then((responseJson) => {
      if(responseJson.error){
        setStatus({
          type: 'erro',
          mensagem: responseJson.mensagem,
        });
        statusLogin(0);
      }else{
        //redirecionar para gerente/funcionario
        statusLogin(responseJson.tipo);
      }
    }).catch(() => {
      setStatus({
        type: 'erro', 
        mensagem: 'naos logado',
      });
      statusLogin(0);
    });
  }
  
    return (
      <Geral>
        
        <Imagemtopo></Imagemtopo>
        <Container>  
          <ConteudoForm>

          <ConteudoTitulo>

          
          </ConteudoTitulo>
          {status.type === 'success'? <AlertSuccess> {status.mensagem} </AlertSuccess>: ""}
          {status.type === 'erro'? <AlertDanger> {status.mensagem} </AlertDanger>: ""}

          
            <Form onSubmit={Login}>
              
              <Input type="text" name="user"  onChange={valorInput} placeholder = "Usuario"/> <br/><br/>

              AjustarInput
              <Input type="text" name="senha" onChange={valorInput} placeholder = "Senha" /> <br/><br/>
              
              <ButtonSuccess type="submit"> Login </ButtonSuccess>

            </Form>
          </ConteudoForm>
        </Container>
      </Geral>
    );
}
