import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { Login } from '../Login';
import { HomeGerente } from '../HomeGerente';
import { HomeFuncionario } from '../HomeFuncionario';

function App() {    
    const [isAuthenticated, setIsAuthenticated] = useState(0);

    const handleStatusLoginChange = (newStatusLogin) => {
        setIsAuthenticated(newStatusLogin);
      
        if (newStatusLogin === 1) {
          window.location.replace("/home-gerente");
        } else if (newStatusLogin === 2) {
          window.location.replace("/home-funcionario");
        }
      };

      function App() {
        return (
          <Router>
            <Routes>
              <Route element={<HomeGerente />} path="/home-gerente" />
              <Route element={<HomeFuncionario />} path="/home-funcionario" />
              <Route element={<Login statusLogin={handleStatusLoginChange} />} path="/" />
            </Routes>
          </Router>
        );
      }
}

export default App;
