import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { Login } from '../Login';
import { HomeGerente } from '../HomeGerente';
import { HomeFuncionario } from '../HomeFuncionario';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(0);

  useEffect(() => {
    if (isAuthenticated === 1) {
      window.location.href = "/gerente";
    } else if (isAuthenticated === 2) {
      window.location.href = "/funcionario";
    }
  }, [isAuthenticated]);

  const handleStatusLoginChange = (newStatusLogin) => {
    setIsAuthenticated(newStatusLogin);
    alert(newStatusLogin);
  };

  return (
    <Router>
      <Routes>
        <Route element={<Login statusLogin={handleStatusLoginChange} />} path="/" exact />
        <Route element={<HomeGerente />} path="/gerente" exact />
        <Route element={<HomeFuncionario />} path="/funcionario" exact />
      </Routes>
    </Router>
  );
}

export default App;
