import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import { Login } from '../Login';
import { HomeGerente } from '../HomeGerente';
import { HomeFuncionario } from '../HomeFuncionario';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(0);

  const handleStatusLoginChange = (newStatusLogin) => {
    setIsAuthenticated(newStatusLogin);
  };

  const PrivateRoutes = ({ element: Element, ...rest }) => {
    if (isAuthenticated === 1) {
      return <Route {...rest} element={<Element />} />;
    } else if (isAuthenticated === 2) {
      return <Route {...rest} element={<Element />} />;
    } else {
      return <Navigate to="/" />;
    }
  };

  return (
    <Router>
      <Routes>
        <PrivateRoutes path="/home-gerente" element={<HomeGerente />} />
        <PrivateRoutes path="/home-funcionario" element={<HomeFuncionario />} />
        <Route path="/" element={<Login statusLogin={handleStatusLoginChange} />} />
      </Routes>
    </Router>
  );
}

export default App;
