import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes} from 'react-router-dom';
import { Login } from '../Login';
import { HomeGerente } from '../HomeGerente';
import { HomeFuncionario } from '../HomeFuncionario';

function App() {
    const [isAuthenticated, setIsAuthenticated] = useState(0);

    const handleStatusLoginChange = (newStatusLogin) => {
        setIsAuthenticated(newStatusLogin);
    };

    let defaultRoute;

    if (isAuthenticated === 1) {
        defaultRoute = <Route element={<HomeGerente />} path="/Home" exact />;
    } else if (isAuthenticated === 2) {
        defaultRoute = <Route element={<HomeFuncionario />} path="/Home" exact />;
    } else {
        defaultRoute = <Route element={<Login statusLogin={handleStatusLoginChange} />} path="/" exact />;
    }

    return (
        <Router>
            <Routes>
                {defaultRoute}
            </Routes>
        </Router>
    );
}

export default App;
