import React, { useState } from 'react';


import { Titulo, ConteudoForm, AlertSuccess, AlertDanger, Form, Container, Label, Input, ButtonSuccess,  ConteudoTitulo} from './styles';


function Login({statusLogin}){

  const [usuario, setUsuario] = useState({
    user: '',
    senha: ''
  })

  const [status, setStatus] = useState({
    type: '',
    mensagem: ''
  })


  
  const valorInput = e => setUsuario({ ...usuario, [e.target.name]: e.target.value});

  const Login = async e =>{
    e.preventDefault();
    

    await fetch("http://localhost/api-crud-php/login.php", {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({usuario})
    })
    .then((response) => response.json())
    .then((responseJson) => {
      if(responseJson.error){
        setStatus({
          type: 'erro',
          mensagem: responseJson.mensagem,
        })
      }else{
        //redirecionar para gerente/funcionario
        statusLogin(responseJson.tipo);
      }
    }).catch(() => {
      setStatus({
        type: 'erro', 
        mensagem: 'naos logado',
      });
    });
  }
  
    return (
      <Container>
        <ConteudoForm>

        <ConteudoTitulo>
          <Titulo> Login </Titulo>

         
        </ConteudoTitulo>
        {status.type === 'success'? <AlertSuccess> {status.mensagem} </AlertSuccess>: ""}
        {status.type === 'erro'? <AlertDanger> {status.mensagem} </AlertDanger>: ""}

        
          <Form onSubmit={Login}>
            <Label> Usuario </Label>
            <Input type="text" name="user"  onChange={valorInput} /> <br/><br/>

            <Label> Senha </Label>
            <Input type="text" name="senha" onChange={valorInput} /> <br/><br/>
            
            <ButtonSuccess type="submit"> Login </ButtonSuccess>

          </Form>
        </ConteudoForm>
      </Container>
    );
}

export default Login;
